mapper.update:
update document
title = #{title}, content = #{content}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where doc_id = #{docId}

mapper.select:
select d.*, u.nickname as ownerName from document d left join user u on d.owner_id = u.id where d.status = 1 and d.doc_id in
#{id}
order by d.update_time desc
